INSERT INTO `s_core_config_mails` VALUES ('19', null, 'sCANCELEDQUESTION', '{config name=mail}', '{config name=shopName}', 'Ihre abgebrochene Bestellung - Jetzt Feedback geben und Gutschein kassieren', '{include file=\"string:{config name=emailheaderplain}\"}\r\n\r\nLieber Kunde,\r\n \r\nSie haben vor kurzem Ihre Bestellung auf {sShop} nicht bis zum Ende durchgeführt - wir sind stets bemüht unseren Kunden das Einkaufen in unserem Shop so angenehm wie möglich zu machen und würden deshalb gerne wissen, woran Ihr Einkauf bei uns gescheitert ist.\r\n \r\nBitte lassen Sie uns doch den Grund für Ihren Bestellabbruch zukommen, Ihren Aufwand entschädigen wir Ihnen in jedem Fall mit einem 5,00 € Gutschein.\r\n \r\nVielen Dank für Ihre Unterstützung.\r\n\r\n{include file=\"string:{config name=emailfooterplain}\"}', '', '0', '', '2', null, '0');
INSERT INTO `s_core_config_mails` VALUES ('20', null, 'sCANCELEDVOUCHER', '{config name=mail}', '{config name=shopName}', 'Ihre abgebrochene Bestellung - Gutschein-Code anbei', '{include file=\"string:{config name=emailheaderplain}\"}\r\n\r\nLieber Kunde,\r\n \r\nSie haben vor kurzem Ihre Bestellung bei {sShop} nicht bis zum Ende durchgeführt - wir möchten Ihnen heute einen 5,00 € Gutschein zukommen lassen - und Ihnen hiermit die Bestell-Entscheidung bei {sShop} erleichtern.\r\n \r\nIhr Gutschein ist 2 Monate gültig und kann mit dem Code \"{$sVouchercode}\" eingelöst werden.\r\n\r\nWir würden uns freuen, Ihre Bestellung entgegen nehmen zu dürfen.\r\n\r\n{include file=\"string:{config name=emailfooterplain}\"}', '', '0', '', '2', null, '0');
